  methods: {
    toggleMenu () {
      this.navIsActive = !this.navIsActive
    },

    closeMenu () {
      if (this.navIsActive) this.navIsActive = false
    }
  },

  created () {
    // Initialise Data
    if (Object.keys(this.$store.state.nav_items).length === 0) this.$store.dispatch('loadNav')
  }
